DROP TABLE IF EXISTS Items;
DROP TABLE IF EXISTS Orders;
DROP TABLE IF EXISTS Products;
DROP TABLE IF EXISTS ProductCategories;
DROP TABLE IF EXISTS Suppliers;
CREATE TABLE Orders (Id INTEGER NOT NULL, TotalPrice REAL, clientFullName TEXT, clientAdress INTEGER);
CREATE TABLE ProductCategories(id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL UNIQUE, name TEXT, department TEXT, description TEXT);
CREATE TABLE Products(id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL UNIQUE, name TEXT, price NUMERIC, description TEXT, currency TEXT, id_productCategory INTEGER, id_supplier INTEGER);
CREATE TABLE Suppliers(id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL UNIQUE, name TEXT, description TEXT);
CREATE TABLE Items (Id INTEGER NOT NULL, BasketId INTEGER NOT NULL, IdProduct INTEGER NOT NULL, Quantity INTEGER NOT NULL);
